import * as React from "react"
export class Login extends React.Component {


  constructor(props){
    super(props)
    this.state = {
      email : "",
      password :"" 
    }
  } 

  handleChangeEmail(e){
    this.setState({
      email:e.target.value
    })
    
  }

  handleChangePassword(e){
    this.setState({
      password:e.target.value
    })
    
  }


  handleSubmit(e){
    e.preventDefault()

  }

  render(){
    return (  <div id="landing">
                <div id="frame-container">
                  <form action="" method="POST" className="text-start js-login-form"  id="frame">
                    <label for="email">Adresse e-mail</label>
                    <input type="email" id="email" name="email" className="form-control" placeholder="mail@provider.com" required autofocus></input>
                    <div className="error">
                      Veuillez renseigner une adresse e-mail
                    </div>
                    <label for="password">Mot de passe</label>
                    <input type="password" id="password" name="password" className="form-control" placeholder="password" required></input>
                    <div className="error">
                      Veuillez renseigner un mot de passe
                    </div>
                    <div className="error text-center">
                      Identifiants incorrects
                    </div>
                    <button type="submit">
                      Connexion
                    </button>
                    <div className="text-center">
                      <a href="oubli_mdp.html" title="J'ai oublié mon mot de passe">J'ai oublié mon mot de passe</a>
                    </div>
                    <div className="text-center">
                      <a href="login.html" title="Pas encore de compte ? Inscrivez-vous !">Pas encore de compte ? Inscrivez-vous !</a>
                    </div>
                  </form>
                </div>
              </div>
            )
  }
}
